meta.custom_css:
.no-effects { 
background: #9bbea9;
}
blockquote {
color: red;
border-left: none;
font-family: 'Libre Baskerville', serif;
font-weight:400; 
font-style:italic;
}
body {
font-size: 13px;
}
h2 {
font-family: 'Libre Baskerville', serif;
font-weight:900; 
font-style:italic;
}

div > a {
    position: relative;
    color: #000;
    text-decoration: none !important;
}

div > a:hover {
  color: #000;
}

div > a::before {
  content: "";
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: 0;
  left: 0;
  background-color: #000;
  visibility: hidden;
  transform: scaleX(0);
  transition: all 0.3s ease-in-out 0s;
}

div > a:hover::before {
  visibility: visible;
  transform: scaleX(1);
}
div > a:visited {
color: #000;
}
