meta.custom_css:
.no-effects {
background: #084787;
}
.no-effects li {
     margin-bottom: 0rem;
    line-height: 1.5em; 
}

blockquote {
color: maroon;
border-left: none;
font-family: 'Libre Baskerville', serif;
font-weight:400; 
font-style:italic;
}
body {
font-size: 13px;
}
h2 {
font-family: 'Libre Baskerville', serif;
font-weight:900; 
font-style:italic;
}

.alert-danger {
    color: #721c24;
    background-color: #f8d7da;
    border-color: #f5c6cb;
}
.alert {
    position: relative;
    padding: .75rem 1.25rem;
    margin-bottom: 1rem;
    border: 1px solid transparent;
    border-radius: .25rem;
}
